  var mongoIcon = require('../Skillbox/icons/mongodb.png')
  var reactIcon = require('../Skillbox/icons/react.png')
  var pythonIcon = require('../Skillbox/icons/python.png')

  const skills = [ //creating an array of objects to cycle through for our carousel
      {
        icon: gitIcon,
        description: 'Git',
      },
      {
        icon: htmlIcon,
        description: 'HTML',
      },
      {
        icon: cssIcon,
        description: 'CSS',
      },
      {
        icon: jsIcon,
        description: 'Javascript',
      },
      {
        icon: nodeIcon,
        description: 'Node.JS',
      },
      {
        icon: mongoIcon,
        description: 'MongoDB',
      },
      {
        icon: reactIcon,
        description: 'React',
      },
      {
        icon: pythonIcon,
        description: 'Python',
